INSERT INTO `#__js_ticket_emailtemplates` (`id`, `templatefor`, `title`, `subject`, `body`, `created`, `status`) VALUES (26,'mail-rpy-closed','',	'JS Tickets: Ticket has been closed',	'<p>Your ticket {TICKET_SUBJECT} has been closed.</p>\n<p>You can not reply to a closed ticket</p>\n<p>	<span style=\"color: red;\">	<strong>*DO NOT REPLY TO THIS E-MAIL*</strong>	</span>	<br />\nThis is an automated e-mail message sent from our support system. Do not reply to this e-mail as we wonot receive your reply!</p>\n',NULL,0),(27,'mail-feedback','','JS Tickets: Give Us Your Feedback','<p>Dear {USER_NAME}, Your ticket {TICKET_SUBJECT} having tracking id {TRACKING_ID} has been closed on {CLOSE_DATE}.\n We would really appreciate if you took the time to tell us how well our staff member helped you in your problem..</p>\n<p>	{LINK}link text{/LINK}	</p>\n<p>	\n	<span style=\"color: red;\">	<strong>*DO NOT REPLY TO THIS E-MAIL*</strong>	</span>	<br />\nThis is an automated e-mail message sent from our support system. Do not reply to this e-mail as we wonot receive your reply!</p>\n',NULL,0);
